(function() {
  var formatState, initAccordion, initCarousel, initMenu, initSelect;

  initMenu = function() {
    $('.menu_btn').on('click', function() {
      return $('body').toggleClass('menu-opened');
    });
    return $(document).mouseup(function(e) {
      var menu;
      menu = $('.navigation, .menu_btn');
      if (!menu.is(e.target) && menu.has(e.target).length === 0) {
        $('body').removeClass('menu-opened');
      }
    });
  };

  initAccordion = function() {
    return $('.faq').accordion();
  };

  formatState = function(state) {
    var $state, baseUrl;
    if (!state.id) {
      return state.text;
    }
    baseUrl = '/dist/assets/images/flags';
    $state = $('<span><img src="' + baseUrl + '/' + state.element.value.toLowerCase() + '.png" class="img-flag" /> ' + '<span>' + state.text + '</span>' + '</span>');
    return $state;
  };

  initSelect = function() {
    return $('.phone_input select').select2({
      templateResult: formatState,
      templateSelection: formatState,
      minimumResultsForSearch: 2e308
    });
  };

  initCarousel = function() {
    return $('.brands').slick({
      infinite: true,
      slidesToShow: 4,
      slidesToScroll: 1,
      dots: false,
      arrows: false,
      autoplay: true,
      autoplaySpeed: 5000
    });
  };

  // ---
  // generated by js2coffee 2.2.0
  $(function() {
    initMenu();
    initAccordion();
    initSelect();
    return initCarousel();
  });

}).call(this);
